SELECT FT.FT_SEQ as ftSeq, FT.FILE_SEVNAME as fileSevname, FT.FT_NAME as ftName, FT.FT_INTRO as ftIntro, USR.USER_SEQ as userSeq FROM ( SELECT FT_SEQ, USER_SEQ FROM FT_REVIEW WHERE REV_POINT = 5 AND USER_SEQ IN
#{item.userSeq}
GROUP BY FT_SEQ, USER_SEQ ORDER BY USER_SEQ, FT_SEQ ) USR INNER JOIN (SELECT FI.FT_SEQ, IMG.FILE_SEVNAME, FI.FT_NAME, FI.FT_INTRO FROM FT_INFO FI INNER JOIN IMAGE IMG ON FI.FILE_ID = IMG.FILE_ID ) FT ON USR.FT_SEQ = FT.FT_SEQ
